import { createContext, useReducer } from "react";

const AppReducer = (state, action) => {
	switch (action.type) {
		case 'ADD_TRANSACTION':
			return {
				...state,
				transactions: [...state.tranactions, action.payload],
			};
		default:
			return state;
	}
};

const initialState = {
    income: 3000,
    transactions: [
        { id: 12, date: '10-02-2023', vendor: 'Target', amount: 40, category:'shopping' },
		{ id: 13, date: '10-01-2023', vendor: 'Exxon', amount: 20, category:'gas' },
        { id: 14, date: '10-01-2023', vendor: 'Rent', amount: 900, category:'rent' },
    ],
};

export const AppContext = createContext();

export const AppProvider = (props) => {
	const [state, dispatch] = useReducer(AppReducer, initialState);

	return (
		<AppContext.Provider
			value={{
				income: state.income,
				transactions: state.transactions,
				dispatch,
			}}
		>
			{props.children}
		</AppContext.Provider>
	);
};
